class ZeroOneReversePattern{	public static void main(String... args){		for(int x=1; x<=5;x++){			for(int y=1; y<=5;y++){				if(x%2==0){						if(y%2==0){						System.out.print("1");					}else{						System.out.print("0");					}				}else{					if(y%2!=0){						System.out.print("1");					}else{						System.out.print("0");					}				}			}			System.out.println();		}	}}